mapper.update:
update user
set last_login_time=NOW()
where ss_number = #{ssNumber};

mapper.select:
select count(id)
from school_stu
where name = #{name}
  and ss_number = #{ssNumber}

select count(ss_number)
from user
where ss_number = #{ssNumber}

select name
from school_stu
where ss_number = #{ssNumber}

select password
from user
where ss_number = #{ssNumber}

mapper.update:
update user
major_name=#{upload_major_name}, college_name=#{upload_college_name}, contact=#{upload_tel}, address=#{upload_address}, introduce=#{upload_introduce}, scholar_introduce=#{upload_scholar_introduce},
where ss_number = #{ssNumber}
